CREATE OR REPLACE FUNCTION obter_se_recoleta_proc_etapa ( nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_amostra_w	varchar(1);
ie_status_w	smallint;
ie_result_w	varchar(1) := 'N';


BEGIN

select	ie_amostra,
		ie_etapa
into STRICT	ie_amostra_w,
		ie_status_w
from prescr_proc_etapa
where nr_prescricao = nr_prescricao_p
  and nr_seq_prescricao  = nr_seq_prescr_p
  and nr_sequencia  = nr_seq_etapa_p;

if (ie_amostra_w = 'N') and (ie_status_w >= 20) then

	/*somente considerar como recoleta caso jรก tenha passado pelo status de coleta em algum momento anterior ao status atual*/

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_result_w
	from	prescr_proc_etapa
	where	nr_prescricao = nr_prescricao_p
	and 	nr_seq_prescricao  = nr_seq_prescr_p
	and		ie_etapa = 20
	and		nr_sequencia < nr_seq_etapa_p;

end if;

return ie_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
