CREATE OR REPLACE FUNCTION obter_se_recoleta ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_amostra_w	varchar(1);
ie_status_w	smallint;
ie_result_w	varchar(1) := 'N';


BEGIN

select	ie_amostra,
	ie_status_atend
into STRICT	ie_amostra_W,
	ie_status_w
from prescr_procedimento
where nr_prescricao = nr_prescricao_p
  and nr_sequencia  = nr_seq_prescr_p;

if (ie_amostra_w = 'N') and (ie_status_w >= 20) then
	ie_result_w := 'S';
end if;

return ie_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
