CREATE OR REPLACE FUNCTION obter_se_recomendacao_alta ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_possui_w	char(1);

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select 	coalesce(max('S') ,'N')
	into STRICT 	ie_possui_w
	from   	tipo_recomendacao b,
			prescr_recomendacao a where		b.cd_tipo_recomendacao = a.cd_recomendacao
	and		coalesce(a.ie_suspenso,'N') = 'N'
	and    	a.nr_prescricao  = nr_prescricao_p
	and    	ie_recomendacao_alta = 'S' LIMIT 1;
end if;

return	ie_possui_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
