CREATE OR REPLACE FUNCTION obter_se_rec_kit_cad ( nr_seq_recomendacao_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

ie_verifica_w	bigint;
ie_retorno_w	varchar(3);

BEGIN
if (nr_seq_recomendacao_p IS NOT NULL AND nr_seq_recomendacao_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	select	count(*)
	into STRICT	ie_verifica_w
	from	kit_prescr_recomendacao
	where	cd_recomendacao = nr_seq_recomendacao_p
	and		nr_prescricao		= nr_prescricao_p;
	if (ie_verifica_w > 0) then
		ie_retorno_w := 'N';
	else
		ie_retorno_w := 'S';
	end if;
else
	ie_retorno_w := 'S';
end if;
return coalesce(ie_retorno_w,'S');
end;
$body$
