CREATE OR REPLACE FUNCTION obter_se_reembolso_pago ( nr_seq_protocolo_conta_p bigint, dt_liquidacao_p text) RETURNS varchar AS $body$
DECLARE


qt_registros_w	bigint;
retorno_w		varchar(1);


BEGIN

select 	count(*)
into STRICT	qt_registros_w
from	titulo_pagar t
where	t.nr_seq_reembolso = nr_seq_protocolo_conta_p
and	to_char(t.dt_liquidacao, 'YYYY') = dt_liquidacao_p;

if (qt_registros_w > 0) then
	retorno_w := 'S';
else
	retorno_w := 'N';
end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
