CREATE OR REPLACE FUNCTION obter_se_refeicao_lib_acomp ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_seq_atecaco_w	bigint;
cd_convenio_w		integer;
ds_retorno_w		varchar(1) := 'S';
dt_entrada_w		timestamp;
					

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	max(dt_entrada) 
	into STRICT	dt_entrada_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
 
	nr_seq_atecaco_w := obter_atecaco_atendimento(nr_atendimento_p);
 
	select	max(cd_convenio) 
	into STRICT	cd_convenio_w 
	from	atend_categoria_convenio 
	where	nr_atendimento	= nr_atendimento_p 
	and	nr_seq_interno = nr_seq_atecaco_w;
	 
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END  
	into STRICT	ds_retorno_w 
	from	mapa_regra_refeicao_acomp 
	where	coalesce(cd_convenio,cd_convenio_w) = cd_convenio_w 
	and	coalesce(ie_situacao,'A') = 'A';	
	 
	if (ds_retorno_w = 'N') then 
	 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_retorno_w 
		from	mapa_regra_refeicao_acomp 
		where	coalesce(cd_convenio,cd_convenio_w) = cd_convenio_w 
		and	(dt_entrada_w + (qt_horas_refeicao/24)) >= clock_timestamp() 
		and	coalesce(ie_situacao,'A') = 'A';
		 
	end if;	
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
