SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_refracao_receita ( nr_seq_consulta_p bigint) RETURNS varchar AS $body$
DECLARE


qt_oft_refracao_w	bigint;
ds_retorno_w		varchar(1) := 'N';


BEGIN

if (nr_seq_consulta_p IS NOT NULL AND nr_seq_consulta_p::text <> '') then
	SELECT	COUNT(*)
	INTO STRICT	qt_oft_refracao_w
	FROM	oft_refracao
	WHERE	nr_seq_consulta = nr_seq_consulta_p
	AND     ((ie_receita_dinamica = 'S') OR (ie_receita_estatica = 'S'))
   AND     coalesce(ie_situacao,'A') = 'A';
end if;

if (qt_oft_refracao_w > 0) then
	 ds_retorno_w := 'S';
else
	 ds_retorno_w := 'N';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
