CREATE OR REPLACE FUNCTION obter_se_registro_atual (nr_seq_registro_p bigint, nm_tabela_p text, nm_pk_tab_p text, ds_restricao_p text) RETURNS varchar AS $body$
DECLARE


ie_atual_w		varchar(1) := 'N';
ds_comando_w		varchar(1000);
ds_restricao_w	varchar(255);
qt_registro_w		double precision;


BEGIN
if (nr_seq_registro_p IS NOT NULL AND nr_seq_registro_p::text <> '') and (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') and (nm_pk_tab_p IS NOT NULL AND nm_pk_tab_p::text <> '') then
	/* montar comando */

	if (coalesce(ds_restricao_p::text, '') = '') then
		ds_restricao_w := '1 = 1';
	else
		ds_restricao_w := ds_restricao_p;
	end if;

	ds_comando_w :=	'select	count(*)	'	||
				' from		' || nm_tabela_p	||
				' where	' || nm_pk_tab_p	|| 	' = ' 			|| nr_seq_registro_p	||
				' and		' || nm_pk_tab_p	||	' = (	select	max('	|| nm_pk_tab_p	|| ') ' ||
										'	from	'	|| nm_tabela_p 	||
										'	where	'	|| ds_restricao_w	|| ') ' ||
				' and		' || ds_restricao_w;

	/* executar comando */

	qt_registro_w := obter_valor_dinamico(ds_comando_w, qt_registro_w);

	/* validar resultado */

	if (qt_registro_w = 1) then
		ie_atual_w := 'S';
	end if;
end if;

return ie_atual_w;

end;
