SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_registro_kit_consist ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(01) := 'S';
qt_existe_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_existe_w
from	kit_estoque b,
	kit_estoque_reg a
where	a.nr_sequencia 	= nr_sequencia_p
and	a.nr_sequencia	= b.nr_seq_reg_kit;

if (qt_existe_w = 0) then
	ds_retorno_w := 'N';
else
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	kit_estoque b,
		kit_estoque_reg a
	where	a.nr_sequencia 	= nr_sequencia_p
	and	a.nr_sequencia	= b.nr_seq_reg_kit
	and 	not exists (	SELECT	1
				from	kit_estoque_comp x
				where	x.nr_seq_kit_estoque = b.nr_sequencia);

	if (qt_existe_w > 0) then
		ds_retorno_w	:= 'N';
	else
		begin
		select	count(*)
		into STRICT	qt_existe_w
		from	kit_estoque_comp c,
			kit_estoque e,
			kit_estoque_reg r
		where	r.nr_sequencia 	= nr_sequencia_p
		and	r.nr_sequencia	= e.nr_seq_reg_kit
		and	e.nr_sequencia	= c.nr_seq_kit_estoque
		and	coalesce(c.ie_gerado_barras,'N') = 'N';

		if (qt_existe_w > 0) then
			ds_retorno_w	:= 'N';
		end if;
		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
