CREATE OR REPLACE FUNCTION obter_se_regra_agrup_conv (cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1):= 'N';
qt_regra_w	bigint;


BEGIN

select 	count(*)
into STRICT	qt_regra_w
from 	regra_agrup_convenio;

if (qt_regra_w > 0) then

	select 	coalesce(max('N'),'S')
	into STRICT	ie_retorno_w
	from 	regra_agrup_convenio
	where 	cd_convenio = cd_convenio_p;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
