CREATE OR REPLACE FUNCTION obter_se_regra_apraz_proc ( nr_sequencia_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE



ie_retorno_w                  varchar(10);

cd_area_procedimento_w	   bigint;
cd_especialidade_w	   bigint;
cd_grupo_proc_w		   bigint;
cd_procedimento_w	   bigint;
nr_seq_proc_interno_w	   bigint;

ie_origem_proced_w	   bigint;

qt_regra_apraz_w	   bigint;



BEGIN

qt_regra_apraz_w    := 0;

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_P IS NOT NULL AND nr_sequencia_P::text <> '') then

	select count(*)
	into STRICT   qt_regra_apraz_w
	from   regra_aprazamento_proc;

	if (qt_regra_apraz_w > 0) then

		select 	max(a.cd_area_procedimento),
			max(a.cd_especialidade),
			max(a.cd_grupo_proc),
			max(c.nr_seq_proc_interno),
			max(a.ie_origem_proced),
			max(a.cd_procedimento)
		into STRICT	cd_area_procedimento_w,
			cd_especialidade_w,
			cd_grupo_proc_w,
			nr_seq_proc_interno_w,
			ie_origem_proced_w,
			cd_procedimento_w
		from	prescr_procedimento c,
			estrutura_procedimento_v a
		where	c.cd_procedimento = a.cd_procedimento
		and	c.ie_origem_proced = a.ie_origem_proced
		and     c.nr_prescricao = nr_prescricao_p
		and     c.nr_sequencia = nr_sequencia_p;


		SELECT coalesce(MAX('S'),'N')
		into STRICT 	ie_retorno_w
		from	regra_aprazamento_proc
		where   coalesce(cd_procedimento, cd_procedimento_w)			= cd_procedimento_w
		and	coalesce(ie_origem_proced, ie_origem_proced_w)		= ie_origem_proced_w
		and	coalesce(cd_area_procedimento, cd_area_procedimento_w)	= cd_area_procedimento_w
		and	coalesce(cd_especialidade, cd_especialidade_w)		= cd_especialidade_w
		and	coalesce(cd_grupo_proc, cd_grupo_proc_w)			= cd_grupo_proc_w
		and	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_w,0))	= coalesce(nr_seq_proc_interno_w,0);


	else
		ie_retorno_w := 'S';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
