CREATE OR REPLACE FUNCTION obter_se_regra_atend_local ( nr_requisicao_p bigint) RETURNS varchar AS $body$
DECLARE


cd_local_estoque_w		local_estoque.cd_local_estoque%type;
cd_perfil_w			perfil.cd_perfil%type;
qt_regra_w			integer := 0;


BEGIN

select	cd_local_estoque,
	obter_perfil_ativo
into STRICT	cd_local_estoque_w,
	cd_perfil_w
from	requisicao_material
where	nr_requisicao = nr_requisicao_p;

select	count(nr_sequencia)
into STRICT	qt_regra_w
from	regra_atend_req_local
where	cd_local_estoque = cd_local_estoque_w
and (cd_perfil = cd_perfil_w or coalesce(cd_perfil::text, '') = '');

return	qt_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
