CREATE OR REPLACE FUNCTION obter_se_regra_consist_sae_lib ( nr_seq_regra_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255)	:= 'S';
qt_reg_w		bigint;
cd_perfil_w		bigint		:= obter_perfil_ativo;


BEGIN
if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '')then
	select	count(*)
	into STRICT	qt_reg_w
	from	consiste_regra_sae_lib
	where	nr_seq_regra_sae = nr_seq_regra_p;

	if (qt_reg_w > 0) then
		select	count(*)
		into STRICT	qt_reg_w
		from	consiste_regra_sae_lib
		where	nr_seq_regra_sae	= nr_seq_regra_p
		and		coalesce(cd_perfil,coalesce(cd_perfil_w,0))	= coalesce(cd_perfil_w,0)
		and		coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0);

		if (qt_reg_w	= 0) then
			ds_retorno_w := 'N';
		end if;
	end if;
end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
