CREATE OR REPLACE FUNCTION obter_se_regra_curta_duracao ( cd_perfil_p bigint, cd_medico_p text, cd_especialidade_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_retorno_w 		char(1) := 'N';
cd_especialidade_w	medico_especialidade.cd_especialidade%type := 0;
cd_perfil_w			perfil.cd_perfil%type	:= 0;
				

BEGIN 
 
select	coalesce(max('S'),'N') 
into STRICT	ie_retorno_w 
from	regra_plt_curta_duracao LIMIT 1;
 
if (ie_retorno_w = 'S') then 
	 
	if (cd_especialidade_p IS NOT NULL AND cd_especialidade_p::text <> '') then 
		cd_especialidade_w	:= cd_especialidade_p;
	elsif (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '') then 
		cd_especialidade_w	:= obter_especialidade_medico2(cd_medico_p);
	end if;
	 
	cd_perfil_w		:= coalesce(cd_perfil_p,0);
	cd_especialidade_w	:= coalesce(cd_especialidade_w,0);
	 
	select	coalesce(max('S'),'N') 
	into STRICT	ie_retorno_w 
	from	regra_plt_curta_duracao 
	where	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w 
	and		coalesce(cd_especialidade,cd_especialidade_w) = cd_especialidade_w  LIMIT 1;
	 
end if;
 
return ie_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
