CREATE OR REPLACE FUNCTION obter_se_regra_diaria_tx ( nr_sequencia_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_reg_w		bigint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
cd_grupo_proc_w		bigint;


BEGIN

select	b.cd_procedimento,
	b.ie_origem_proced,
	b.cd_area_procedimento,
	b.cd_especialidade,
	b.cd_grupo_proc
into STRICT	cd_procedimento_w,
	ie_origem_proced_w,
	cd_area_procedimento_w,
	cd_especialidade_w,
	cd_grupo_proc_w
from	procedimento_paciente a,
	estrutura_procedimento_v b
where	a.cd_procedimento = b.cd_procedimento
and	a.ie_origem_proced = b.ie_origem_proced
and	a.nr_sequencia = nr_sequencia_p;

select	count(*)
into STRICT	qt_reg_w
from	conv_regra_diaria_tx_crit
where	coalesce(cd_procedimento, cd_procedimento_w) = cd_procedimento_w
and	coalesce(ie_origem_proced, ie_origem_proced_w) = ie_origem_proced_w
and	coalesce(cd_area_procedimento, cd_area_procedimento_w) = cd_area_procedimento_w
and	coalesce(cd_especialidade, cd_especialidade_w) = cd_especialidade_w
and	coalesce(cd_grupo_proc, cd_grupo_proc_w) = cd_grupo_proc_w
and	nr_seq_regra_diaria = nr_seq_regra_p;

if (qt_reg_w > 0) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
