CREATE OR REPLACE FUNCTION obter_se_regra_diluicao_setor ( nr_seq_diluicao_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_gerar_diluicao_w	varchar(2) := 'S';
cont_w			bigint;
agrup_setor_w   bigint;


BEGIN

select Obter_agrupamento_class_setor(cd_setor_atendimento_p)
into STRICT agrup_setor_w
;

select	count(*)
into STRICT	cont_w
from	regra_liberacao_diluicao
where	nr_seq_diluicao		= nr_seq_diluicao_p
and	   ((cd_setor_excluir	= cd_setor_atendimento_p) or (coalesce(cd_setor_excluir::text, '') = ''))
and    ((NR_SEQ_GRUPO_SETOR = agrup_setor_w) or (coalesce(NR_SEQ_GRUPO_SETOR::text, '') = ''));

if (cont_w > 0) then
	ie_gerar_diluicao_w	:= 'N';
end if;

return	coalesce(ie_gerar_diluicao_w,'S');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
