CREATE OR REPLACE FUNCTION obter_se_regra_envio_intern ( nr_seq_regra_p bigint, nr_atendimento_p bigint, ie_novo_atendimento_p text) RETURNS varchar AS $body$
DECLARE


ie_regra_w		varchar(1) := 'S';
qt_registros_w		bigint;


BEGIN
select	count(*)
into STRICT	qt_registros_w
from	regra_envio_sms_intern
where	nr_seq_regra	= nr_seq_regra_p;

if (qt_registros_w > 0) then
	select	coalesce(MAX('S'),'N')
	into STRICT	ie_regra_w
	from	regra_envio_sms_intern
	where	nr_seq_regra	= nr_seq_regra_p
	and	((ie_novo_atendimento = 'M') and (ie_novo_atendimento_p = 'N') or -- Mesmo atendimento
		(ie_novo_atendimento = 'S' AND ie_novo_atendimento_p = 'S')); -- Novo atendimento
end if;

return	ie_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
