CREATE OR REPLACE FUNCTION obter_se_regra_escala_indice ( nr_seq_grupo_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

qt_reg_w			bigint;
cd_setor_atual_w	bigint;
ie_permite_w		varchar(10);
nm_usuario_w		varchar(15);

C01 CURSOR FOR 
	SELECT	coalesce(IE_PERMITE,'S') 
	from	GRUPO_ESCALA_INDICE_REGRA 
	where	nr_seq_grupo	= nr_seq_grupo_p 
	and	ie_situacao	='A' 
	and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_p,cd_especialidade)	= 'S')) 
	and	coalesce(cd_setor_atendimento,cd_setor_atual_w)	= cd_setor_atual_w 
	and	coalesce(nm_usuario_param,nm_usuario_w	) = nm_usuario_w 
	order by coalesce(cd_especialidade,0),coalesce(cd_setor_atendimento,0);


BEGIN 
cd_setor_atual_w	:= coalesce(wheb_usuario_pck.get_cd_setor_atendimento,0);
select	count(*) 
into STRICT	qt_reg_w 
from	GRUPO_ESCALA_INDICE_REGRA 
where	nr_seq_grupo	= nr_seq_grupo_p 
and	ie_situacao	= 'A';
 
select	max(nm_usuario) 
into STRICT	nm_usuario_w 
from	usuario 
where	cd_pessoa_fisica = cd_pessoa_fisica_p;
 
if (qt_reg_w	> 0) then 
 
	open C01;
	loop 
	fetch C01 into	 
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
 
	return coalesce(ie_permite_w,'N');
	 
end if;
 
 
return	'S';
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
