CREATE OR REPLACE FUNCTION obter_se_regra_evento_pepo ( nr_seq_regra_p bigint, cirurgia_p bigint, nr_seq_evento_p bigint) RETURNS char AS $body$
DECLARE


ie_retorno_w			char(1);
nr_seq_proc_interno_w	proc_interno.nr_sequencia%type;

BEGIN

if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') then
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	regra_informacao_pepo where		nr_seq_regra	= nr_seq_regra_p LIMIT 1;

	if (ie_retorno_w = 'S') then

		select coalesce(max(nr_seq_proc_interno),0)
		into STRICT   nr_seq_proc_interno_w
		from   cirurgia
		where  nr_cirurgia = cirurgia_p;

		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	regra_informacao_pepo where		nr_seq_regra	= nr_seq_regra_p
		and 	coalesce(nr_seq_proc_interno,nr_seq_proc_interno_w) = nr_seq_proc_interno_w
		and		coalesce(nr_seq_evento,nr_seq_evento_p) = nr_seq_evento_p LIMIT 1;

		return ie_retorno_w;
	end if;
end if;

return	'S';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
