CREATE OR REPLACE FUNCTION obter_se_regra_evento_sae ( nr_seq_resultado_p bigint, nr_seq_item_examinar_p bigint, nr_seq_evento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			smallint := 'N';
qt_reg_w				bigint;
cd_estabelecimento_w	smallint;
cd_perfil_w				bigint;


BEGIN

cd_estabelecimento_w		:= coalesce(wheb_usuario_pck.get_cd_estabelecimento,0);
cd_perfil_w					:= coalesce(obter_perfil_ativo,0);

select 	count(*)
into STRICT	qt_reg_w
from	regra_eventos_result_sae
where	coalesce(cd_estabelecimento,cd_estabelecimento_w)	= cd_estabelecimento_w
and		coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w
and		nr_seq_resultado = nr_seq_resultado_p
and		nr_seq_item_examinar = nr_seq_item_examinar_p
and		nr_seq_evento = nr_seq_evento_p;

if (qt_reg_w > 0 ) then
	ds_retorno_w := 'S';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
