CREATE OR REPLACE FUNCTION obter_se_regra_fat_acesso ( nr_seq_regra_fat_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2)	:= 'S';
qt_regra_fat_acesso_w		integer;
cd_perfil_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_regra_fat_acesso_w
from	pls_regra_fat_acesso
where	nr_seq_regra_fat = nr_seq_regra_fat_p;

cd_perfil_w	:= obter_perfil_ativo;

if (qt_regra_fat_acesso_w > 0) then

	select	count(*)
	into STRICT	qt_regra_fat_acesso_w
	from	pls_regra_fat_acesso
	where	nr_seq_regra_fat 	= nr_seq_regra_fat_p
	and	cd_perfil		= cd_perfil_w;

	if (qt_regra_fat_acesso_w = 0) then
		ds_retorno_w	:= 'N';
	end if;

	if (ds_retorno_w = 'N') then
		select	count(*)
		into STRICT	qt_regra_fat_acesso_w
		from	pls_regra_fat_acesso
		where	nr_seq_regra_fat 	= nr_seq_regra_fat_p
		and	nm_usuario_param	= nm_usuario_p;

		if (qt_regra_fat_acesso_w = 0) then
			ds_retorno_w	:= 'N';
		else
			ds_retorno_w	:= 'S';
		end if;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
