CREATE OR REPLACE FUNCTION obter_se_regra_geracao_301 ( nr_seq_episodio_p bigint, dt_referencia_p timestamp, ie_dataset_p text, cd_convenio_p bigint default null) RETURNS varchar AS $body$
DECLARE


ie_gerar_w	varchar(1)	:= 'N';

/* Regras de geração de datasets */

c_regra_geracao CURSOR FOR
SELECT	a.*
from	c301_regra_geracao a
where	a.dt_inicio_vigencia <= dt_referencia_p
and (a.dt_fim_vigencia >= coalesce(dt_referencia_p,to_date('31/12/9999','dd/mm/yyyy')) or coalesce(a.dt_fim_vigencia::text, '') = '')
and	a.ie_dataset_geracao = ie_dataset_p;

BEGIN
for	r_c_regra_geracao in c_regra_geracao loop
	select	CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END  ie_gerar
	into STRICT	ie_gerar_w
	from	atendimento_paciente a,
		episodio_paciente b,
		atend_categoria_convenio c,
		convenio d
	where	a.nr_seq_episodio		= b.nr_sequencia
	and	a.nr_atendimento		= c.nr_atendimento
	and	c.cd_convenio			= d.cd_convenio
	and	c.cd_convenio			= cd_convenio_p
	and	b.nr_sequencia			= nr_seq_episodio_p
	and (b.nr_seq_tipo_episodio 	= r_c_regra_geracao.nr_seq_tipo_episodio or coalesce(r_c_regra_geracao.nr_seq_tipo_episodio::text, '') = '')
	and (b.nr_seq_subtipo_episodio 	= r_c_regra_geracao.nr_seq_subtipo_episodio or coalesce(r_c_regra_geracao.nr_seq_subtipo_episodio::text, '') = '')
	and (d.ie_tipo_convenio		= r_c_regra_geracao.ie_tipo_convenio or coalesce(r_c_regra_geracao.ie_tipo_convenio::text, '') = '')
	and (c.cd_convenio			= r_c_regra_geracao.cd_convenio or coalesce(r_c_regra_geracao.cd_convenio::text, '') = '')
	and (exists (SELECT	1
			from	convenio_classif x
			where	x.cd_convenio		= c.cd_convenio
			and	x.nr_seq_classificacao	= r_c_regra_geracao.nr_seq_classif_conv) or coalesce(r_c_regra_geracao.nr_seq_classif_conv::text, '') = '')
	and (a.ie_tipo_atendimento = r_c_regra_geracao.ie_tipo_atendimento or coalesce(r_c_regra_geracao.ie_tipo_atendimento::text, '') = '')
	and (a.nr_seq_tipo_admissao_fat = r_c_regra_geracao.nr_seq_tipo_adm_fat or coalesce(r_c_regra_geracao.nr_seq_tipo_adm_fat::text, '') = '')
	and (a.nr_seq_classificacao = r_c_regra_geracao.nr_seq_classif_atend or coalesce(r_c_regra_geracao.nr_seq_classif_atend::text, '') = '')
	and (a.nr_seq_queixa = r_c_regra_geracao.nr_seq_queixa_atend or coalesce(r_c_regra_geracao.nr_seq_queixa_atend::text, '') = '');
end loop;

return	ie_gerar_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
