CREATE OR REPLACE FUNCTION obter_se_regra_importacao ( nr_seq_grupo_p bigint, nr_filial_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_estabelecimento_w	smallint;


BEGIN

select	max(a.cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	grupo_bandeira_cr a
where	a.nr_sequencia	= nr_seq_grupo_p;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ds_retorno_w
from	bandeira_cartao_cr_regra a
where	a.nr_seq_grupo		= nr_seq_grupo_p
and	clock_timestamp()			between coalesce(a.dt_inicio_vigencia,clock_timestamp()) and coalesce(a.dt_fim_vigencia,clock_timestamp())
and	coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_w,0))	= coalesce(cd_estabelecimento_w,0)
and	a.nr_filial		= nr_filial_p;

if (ds_retorno_w	= 'N') then
	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	bandeira_cartao_cr_regra a
	where	a.nr_seq_grupo		= nr_seq_grupo_p
	and	clock_timestamp()			between coalesce(a.dt_inicio_vigencia,clock_timestamp()) and coalesce(a.dt_fim_vigencia,clock_timestamp())
	and	coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_w,0))	= coalesce(cd_estabelecimento_w,0)
	and	(a.nr_filial IS NOT NULL AND a.nr_filial::text <> '');
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
