CREATE OR REPLACE FUNCTION obter_se_regra_inf_atributo ( dt_registro_p timestamp, ie_regra_p text) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(10)	:= 'S';

BEGIN
if (ie_regra_p	<> 'N') then

	ie_retorno_w	:= 'N';

	if (ie_regra_p = 'A') and (trunc(dt_registro_p)	= trunc(clock_timestamp() - interval '1 days')) then
		ie_retorno_w	:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
