CREATE OR REPLACE FUNCTION obter_se_regra_int_vigente ( cd_material_p bigint, ie_sistema_externo_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w		integer;
ie_retorno_w		varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	conv_mat_sistema_externo
where	(cd_sistema_externo IS NOT NULL AND cd_sistema_externo::text <> '')
and	cd_material = cd_material_p
and	ie_sistema = ie_sistema_externo_p
and	cd_estabelecimento = cd_estabelecimento_p
and	((clock_timestamp() between dt_inicio_vigencia and dt_fim_vigencia) or (coalesce(dt_inicio_vigencia::text, '') = '' or coalesce(dt_fim_vigencia::text, '') = ''));

if (qt_existe_w > 0) then
	ie_retorno_w := 'S';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
