CREATE OR REPLACE FUNCTION obter_se_regra_leitor_cartao ( nm_usuario_p text, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_computador_p text) RETURNS varchar AS $body$
DECLARE


-- Atributos
ie_retorno_w            varchar(1) :='N';
nr_sequencia_regra_w    bigint := 0;
qt_regras_w             bigint := 0;
ie_cartao_id_w          bigint := 0;

-- Cursores
c_regras CURSOR FOR
SELECT  a.ie_tipo_cartao
from    regra_leitor_cartao_pac a
where   coalesce(a.nm_computador,coalesce(nm_computador_p,'XPTO')) = coalesce(nm_computador_p,'XPTO')
and     coalesce(a.nm_usuario_regra,coalesce(nm_usuario_p,'XPTO')) = coalesce(nm_usuario_p,'XPTO')
and     coalesce(a.cd_perfil,coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
and     coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
order by
        coalesce(a.cd_estabelecimento,0),
        coalesce(a.cd_perfil,0),
        a.nm_usuario_regra nulls first,
        a.nm_computador nulls first;

c_parametro CURSOR FOR
 SELECT  a.ie_cartao_id
from    parametros_episodio a
where   1 = 1
and     coalesce(a.cd_perfil,coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
and     coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0)
order by coalesce(cd_estabelecimento,0),
        coalesce(cd_perfil,0);

-- Funcao
BEGIN
    select count(*)
    into STRICT qt_regras_w
    from regra_leitor_cartao_pac a
    where   coalesce(a.nm_usuario_regra,coalesce(nm_usuario_p,'XPTO')) = coalesce(nm_usuario_p,'XPTO')
    and	    coalesce(a.nm_computador,coalesce(nm_computador_p,'XPTO')) = coalesce(nm_computador_p,'XPTO')
    and     coalesce(a.cd_perfil,coalesce(cd_perfil_p,0)) = coalesce(cd_perfil_p,0)
    and     coalesce(a.cd_estabelecimento,coalesce(cd_estabelecimento_p,0)) = coalesce(cd_estabelecimento_p,0);

    -- Verificar Regras
    if (qt_regras_w>0)then
        begin
            for linha_cursor in c_regras loop      
                 ie_cartao_id_w := linha_cursor.ie_tipo_cartao;
            end loop;
        exception
                when others then
                    ie_cartao_id_w := 0;
        end;

        if (ie_cartao_id_w> 0 )then
            ie_retorno_w := 'S';
        end if;
    -- Verificar Parametros
    else
       for linha_cursor in c_parametro loop      
                ie_cartao_id_w := linha_cursor.ie_cartao_id;
            end loop;

        if (ie_cartao_id_w> 0 )then
            ie_retorno_w := 'S';
        end if;
    end if;

return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
