CREATE OR REPLACE FUNCTION obter_se_regra_lib_interd ( nr_seq_motivo_p bigint, cd_pessoa_fisica_p text, cd_perfil_p bigint, cd_setor_atend_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10) := 'S';
qt_existe_w		bigint;

cd_pessoa_fisica_w	varchar(20);
cd_perfil_w		bigint;
cd_setor_atend_w	bigint;

BEGIN

select 	count(*)
into STRICT	qt_existe_w
from	regra_mot_interd_leito
where	nr_seq_motivo = nr_seq_motivo_p
and	coalesce(ie_situacao,'I') = 'A';

if (coalesce(qt_existe_w,0) > 0) then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	regra_mot_interd_leito
	where	nr_seq_motivo = nr_seq_motivo_p
	and	coalesce(ie_situacao,'I') = 'A'
	and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_p) 	= cd_pessoa_fisica_p
	and	coalesce(cd_perfil,cd_perfil_p)			= cd_perfil_p
	and	coalesce(cd_setor_atendimento,cd_setor_atend_p) 	= cd_setor_atend_p;

end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
