CREATE OR REPLACE FUNCTION obter_se_regra_lib_setor_higie ( cd_setor_p bigint, cd_perfil_p bigint, nm_usuario_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(1);

BEGIN

if (ie_opcao_p = 0) then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	regra_permite_higienizar
	where	cd_perfil = cd_perfil_p
	and	ie_permite_inicio_higienizar = 'S'
	and	cd_setor_atendimento = cd_setor_p;

	if (ds_retorno_w = 'N') then

		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	regra_permite_higienizar
		where	nm_usuario_regra = nm_usuario_p
		and	ie_permite_inicio_higienizar = 'S'
		and	cd_setor_atendimento = cd_setor_p;

	end if;
end if;

if (ie_opcao_p = 1) then

	select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	regra_permite_higienizar
	where	cd_perfil = cd_perfil_p
	and	ie_permite_fim_higienizar = 'S'
	and	cd_setor_atendimento = cd_setor_p;

	if (ds_retorno_w = 'N') then


		select 	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	regra_permite_higienizar
		where	nm_usuario_regra = nm_usuario_p
		and	ie_permite_fim_higienizar = 'S'
		and	cd_setor_atendimento = cd_setor_p;

	end if;
end if;

return	ds_retorno_w;

end;
