SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_regra_med_crit_rep (cd_medico_prescr_p text, ie_regra_medico_p text) RETURNS varchar AS $body$
DECLARE



ds_retorno_w		varchar(100);
ie_corpo_clinico_w	varchar(100);


BEGIN

ds_retorno_w		:= 'S';

if (coalesce(ie_regra_medico_p, 'X') = '1') then -- Edgar 16/03/2009, tratar regra para somente considerar se o médico da prescrição NÃO for do corpo clínico
	select	ie_corpo_clinico
	into STRICT	ie_corpo_clinico_w
	from	medico
	where	cd_pessoa_fisica	= cd_medico_prescr_p;

	ds_retorno_w			:= 'S';
	if (coalesce(ie_corpo_clinico_w, 'N') = 'S') then
		ds_retorno_w		:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
