CREATE OR REPLACE FUNCTION obter_se_regra_mens_contrato ( nr_seq_contrato_p bigint, dt_contratacao_p bigint) RETURNS bigint AS $body$
DECLARE


qt_existe_w	bigint := 0;


BEGIN

if (coalesce(nr_seq_contrato_p,0) > 0) then

	select	count(*)
	into STRICT	qt_existe_w
	from	pls_regra_mens_contrato
	where	nr_seq_contrato = nr_seq_contrato_p
	and	ie_tipo_data_limite = 'A'
	and	dt_limite_movimentacao < (dt_contratacao_p)::numeric;

end if;

return	qt_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
