CREATE OR REPLACE FUNCTION obter_se_regra_ordem ( cd_estabelecimento_p bigint, dt_prevista_p timestamp, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE

ie_regra_w		varchar(1);
nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT 	nr_sequencia
	from	regra_etapa_quimioterapia
	where	cd_estabelecimento = cd_estabelecimento_p
	and 	dt_prevista_p 	between to_date(to_char(clock_timestamp(),'dd/mm/yyyy') ||' '|| to_char(hr_inicial,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
				and 	to_date(to_char(clock_timestamp(),'dd/mm/yyyy') ||' '|| to_char(hr_final,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss');


BEGIN

ie_regra_w := 'N';

open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (nr_seq_regra_p = nr_sequencia_w) then
		ie_regra_w := 'S';
	end if;
	end;
end loop;
close C01;


return	ie_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
