CREATE OR REPLACE FUNCTION obter_se_regra_perm_item_autor (ie_tipo_autorizacao_p text, ie_tipo_item_autor_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

ie_permite_w	varchar(15) := 'S';
qt_existe_w	bigint;			
			
C01 CURSOR FOR
	SELECT	'S'
	from	regra_permissao_item_autor	
	where	cd_estabelecimento = cd_estabelecimento_p
	and	((ie_material = 'S' AND ie_tipo_item_autor_p = '2') or
		(ie_procedimento = 'S' AND ie_tipo_item_autor_p = '1'))
	and	ie_tipo_autorizacao = ie_tipo_autorizacao_p
	and	coalesce(ie_situacao,'A') = 'A'
	order by ie_tipo_autorizacao,
		nr_sequencia;
	
/*
1 - Procedimentos
2 - Materiais
*/
BEGIN

select	count(*)
into STRICT	qt_existe_w
from	regra_permissao_item_autor
where	ie_tipo_autorizacao = ie_tipo_autorizacao_p
and	cd_estabelecimento =  cd_estabelecimento_p
and	((ie_material = 'S' AND ie_tipo_item_autor_p = '2') or
	(ie_procedimento = 'S' AND ie_tipo_item_autor_p = '1'))
and	coalesce(ie_situacao,'A') = 'A';

if ( qt_existe_w > 0) then
	ie_permite_w := 'N';
	open C01;
	loop
	fetch C01 into	
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if ( coalesce(ie_permite_w::text, '') = '') then
			begin
			ie_permite_w := 'N';
			end;
		end if;
		end;
	end loop;
	close C01;
else
	ie_permite_w := 'S';
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
