SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_regra_pf_desc ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ie_retorno_w	varchar(1) := 'S';

/*
S - Solicitante.
A - Autorizador
*/
BEGIN

select	count(*)
into STRICT	qt_existe_w
from	regra_pf_desc;

if (qt_existe_w > 0) and (ie_opcao_p = 'S') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	regra_pf_desc
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(ie_solicitante,'S') = 'S';

elsif (qt_existe_w > 0) and (ie_opcao_p = 'A') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	regra_pf_desc
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	coalesce(ie_autorizador,'S') = 'S';

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
