CREATE OR REPLACE FUNCTION obter_se_regra_pront_lib ( nr_seq_lib_pront_p bigint, cd_perfil_p bigint, cd_especialidade_p bigint, nr_seq_equipe_p bigint, cd_profissional_p bigint) RETURNS varchar AS $body$
DECLARE


		ds_retorno_w		varchar(1) := 'S';

c01 CURSOR FOR
	SELECT	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	from	pf_lib_pront_regra b
	where	b.nr_seq_lib_pront 	= nr_seq_lib_pront_p
	and		clock_timestamp() between coalesce(b.dt_inicio,clock_timestamp()) and coalesce(b.dt_fim,clock_timestamp())
	and		(((b.cd_perfil IS NOT NULL AND b.cd_perfil::text <> '') and b.cd_perfil = obter_perfil_ativo) or ((b.cd_especialidade IS NOT NULL AND b.cd_especialidade::text <> '') and obter_se_especialidade_medico(cd_profissional_p, b.cd_especialidade) = 'S') or ((b.nr_seq_equipe IS NOT NULL AND b.nr_seq_equipe::text <> '') and obter_se_medico_equipe(b.nr_seq_equipe, cd_profissional_p) = 'S') or ((b.cd_pessoa_fisica IS NOT NULL AND b.cd_pessoa_fisica::text <> '') and b.cd_pessoa_fisica = cd_profissional_p));



BEGIN

	select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
	into STRICT	ds_retorno_w
	from	pf_lib_pront_regra b
	where	b.nr_seq_lib_pront 	= nr_seq_lib_pront_p
	and		clock_timestamp() between coalesce(b.dt_inicio,clock_timestamp()) and coalesce(b.dt_fim,clock_timestamp());

	if (ds_retorno_w = 'N') then
		open C01;
		loop
		fetch C01 into
			ds_retorno_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			ds_retorno_w	:= ds_retorno_w;
			end;
		end loop;
		close C01;
	end if;

return ds_retorno_w;

end;
