CREATE OR REPLACE FUNCTION obter_se_regra_rec_alerta (cd_recomendacao_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_recebe_w				varchar(1);
qt_existe_regra_w		bigint;


BEGIN

ie_recebe_w := 'S';

select	count(*)
into STRICT	qt_existe_regra_w
from	regra_envio_sms_rec
where	nr_seq_regra = nr_seq_regra_p;

if (qt_existe_regra_w = 0) then
	ie_recebe_w := 	'S';
else
	select	coalesce(max('S'),'N')
	into STRICT	ie_recebe_w
	from	regra_envio_sms_rec
	where	coalesce(cd_recomendacao,coalesce(cd_recomendacao_p,0)) = coalesce(cd_recomendacao_p,0)
	and		nr_seq_regra = nr_seq_regra_p;
end if;

return	ie_recebe_w;

end;
$body$
