CREATE OR REPLACE FUNCTION obter_se_regra_setor_area (cd_setor_atendimento_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_registros_w		bigint;


BEGIN
ds_retorno_w := 'S';

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_setor_atendimento_p IS NOT NULL AND cd_setor_atendimento_p::text <> '') then

	select 		 count(*)
	into STRICT		 qt_registros_w
	from 		 adep_area_prep_setor
	where 		 nr_seq_area = nr_sequencia_p;

	if (qt_registros_w > 0) then

		select 	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	adep_area_prep_setor
		where	nr_seq_area	=	nr_sequencia_p
		and		cd_setor_atendimento = 	cd_setor_atendimento_p;

	else
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
