CREATE OR REPLACE FUNCTION obter_se_regra_setor_intervalo ( cd_setor_atendimento_p bigint, cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);


BEGIN

select	coalesce(max('N'),'S')
into STRICT	ie_retorno_w
from	rep_regra_setor_intervalo
where	cd_setor_atendimento = cd_setor_atendimento_p;

if (ie_retorno_w = 'N') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	rep_regra_setor_intervalo
	where	cd_intervalo = cd_intervalo_p
	and	cd_setor_atendimento = cd_setor_atendimento_p;

end if;

return	ie_retorno_w;

end;
$body$
