CREATE OR REPLACE FUNCTION obter_se_regra_texto_lib ( nr_seq_item_pront_p bigint, ie_tipo_p text default 'I') RETURNS varchar AS $body$
DECLARE


qt_reg_w	bigint;
ie_permite_w	varchar(10);
ie_tipo_w	varchar(10)	:= coalesce(ie_tipo_p,'I');

C01 CURSOR FOR
	SELECT	ie_permite
	from	regra_texto_padrao_lib
	where	nr_seq_item_pront = nr_seq_item_pront_p
	and	coalesce(cd_perfil,obter_perfil_ativo) = obter_perfil_ativo
	and	IE_TIPO_TEXTO		= ie_tipo_w
	order by coalesce(cd_perfil,0);


BEGIN

if (nr_seq_item_pront_p IS NOT NULL AND nr_seq_item_pront_p::text <> '') then
	select	count(*)
	into STRICT	qt_reg_w
	from	regra_texto_padrao_lib
	where	nr_seq_item_pront 	= nr_seq_item_pront_p
	and	IE_TIPO_TEXTO		= ie_tipo_w;

	if (qt_reg_w > 0) then
		/*select	count(*)
		into	qt_reg_w
		from	regra_texto_padrao_lib
		where	nr_seq_item_pront = nr_seq_item_pront_p
		and	cd_perfil = obter_perfil_ativo;
		*/
		open C01;
		loop
		fetch C01 into
			ie_permite_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		end loop;
		close C01;

		return coalesce(ie_permite_w,'N');
		if (qt_reg_w = 0) then
			return 'N';
		end if;
	end if;
end if;

return	'S';

end;
$body$
