CREATE OR REPLACE FUNCTION obter_se_regra_tipo_doc_lib (nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


cd_perfil_w		perfil.cd_perfil%type := obter_perfil_ativo;
ds_retorno_w		varchar(1) := 'S';
qt_registro_w		bigint;


BEGIN
if (nr_seq_regra_p > 0) then
	select	count(*)
	into STRICT	qt_registro_w
	from	regra_tipo_doc_obr_pf_lib
	where	nr_seq_regra_tipo_doc = nr_seq_regra_p;

	if (qt_registro_w > 0) then
		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	regra_tipo_doc_obr_pf_lib
		where	nr_seq_regra_tipo_doc 	= nr_seq_regra_p
		and	cd_perfil 		= cd_perfil_w  LIMIT 1;

		exception
		when others then
			ds_retorno_w := 'N';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
