CREATE OR REPLACE FUNCTION obter_se_regra_trans_setor ( cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w varchar(1);
qt_regra_w		bigint;
nr_seq_atepacu_w		bigint;


BEGIN

ie_retorno_w := 'S';

select count(*)
into STRICT qt_regra_w
from trans_regra_setor
where cd_setor_atendimento = cd_setor_atendimento_p;

if (qt_regra_w = 0) then
	return ie_retorno_w;
else
	select CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT ie_retorno_w
	from trans_regra_setor
	where ie_situacao = 'A'
	and (cd_estabelecimento = OBTER_ESTABELECIMENTO_ATIVO or coalesce(cd_estabelecimento::text, '') = '')
	and cd_setor_atendimento = cd_setor_atendimento_p;

return ie_retorno_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
