CREATE OR REPLACE FUNCTION obter_se_regulacao_liberada ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_liberada_w		varchar(1);
qt_existe_w		smallint;


BEGIN

ie_liberada_w	:= 'N';
select	count(*)
into STRICT	qt_existe_w
from	eme_regulacao a
where	a.nr_sequencia	= nr_sequencia_p
and exists (
	SELECT	1
	from	eme_reg_resposta b
	where	b.nr_seq_reg = a.nr_sequencia
	and	(b.dt_chegada_base IS NOT NULL AND b.dt_chegada_base::text <> ''));

if (qt_existe_w > 0) then
	ie_liberada_w	:= 'S';
end if;

return ie_liberada_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
