CREATE OR REPLACE FUNCTION obter_se_regulacao_regra_lib ( ie_somente_ref_p text, nr_atendimento_p bigint, nr_seq_regra_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 	varchar(1) := 'N';
ie_regra_lib_w	varchar(1);
cd_convenio_w	integer;
							

BEGIN

ds_retorno_w := coalesce(ie_somente_ref_p,'N');

if (coalesce(ie_somente_ref_p,'N') = 'N') then
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	
			Select  max(obter_convenio_atendimento(nr_atendimento_p))
			into STRICT	cd_convenio_w
			;
	
			select	coalesce(max('S'),'N')
			into STRICT	ds_retorno_w
			from	convenio_regulacao
			where 	cd_convenio = cd_convenio_w
			and		coalesce(ie_situacao, 'A') = 'A';
			
			if (ds_retorno_w = 'S') then
			
				select	coalesce(max('S'),'N')
				into STRICT	ie_regra_lib_w
				from	regra_reg_itens_lib
				where	nr_seq_regra_item = nr_seq_regra_p;
				
				if (ie_regra_lib_w = 'S') then
				
					select	coalesce(max('S'),'N')
					into STRICT	ie_regra_lib_w
					from	regra_reg_itens_lib
					where	nr_seq_regra_item = nr_seq_regra_p
					and		cd_convenio_regulacao = cd_convenio_w;
				
				end if;
					
			end if;
				
	end if;		

end if;
	
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
