CREATE OR REPLACE FUNCTION obter_se_reg_atual_adep ( NR_SEQ_REGISTRO_P bigint, NM_TABELA_P text, NR_ATENDIMENTO_P bigint, NR_SEQ_GLICEMIA_P bigint, NR_SEQ_PROTOCOLO_P bigint, NR_GLICEMIA_ATEND_P bigint, IE_OPCAO_P text ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
ds_restricoes_w	varchar(2000) := '';

-- Esta function é utilizada para habilitar a opção de mouse lado direito Conf_Adm_CCG_mi(
BEGIN

if (coalesce(IE_OPCAO_P, 'C') = 'C') then --Confirmar administração (CCG)
	ds_restricoes_w	:= 	' nr_atendimento = ' || to_char(nr_atendimento_p) ||
				' and nr_seq_glicemia = ' || to_char(nr_seq_glicemia_p) ||
				' and nr_seq_protocolo = ' || to_char(nr_seq_protocolo_p) ||
				' and nr_glicemia_atend = ' || to_char(nr_glicemia_atend_p);

else  --Modificar intervalo (CIG)
	ds_restricoes_w	:= 	' nr_atendimento = ' || to_char(nr_atendimento_p) ||
				' and nr_seq_glicemia = ' || to_char(nr_seq_glicemia_p);

end if;

select 	obter_se_registro_atual(nr_seq_registro_p, nm_tabela_p, 'NR_SEQUENCIA', ds_restricoes_w)
into STRICT	ds_retorno_w
;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
