CREATE OR REPLACE FUNCTION obter_se_reg_conv_pl_cat ( cd_convenio_p bigint, ie_opcao_p text, cd_categoria_p bigint default null, cd_plano_p text default null) RETURNS varchar AS $body$
DECLARE


qt_regra_plano_w	varchar(10);
qt_regra_Categoria_w	varchar(10);
qt_regra_conv_w		bigint;


BEGIN

If (cd_convenio_p IS NOT NULL AND cd_convenio_p::text <> '') then

	If (ie_opcao_p = 'C') then

		select	count(*)
		into STRICT	qt_regra_conv_w
		from	convenio_doc_atend
		where	cd_convenio = cd_convenio_p
		and	coalesce(cd_categoria::text, '') = ''
		and	coalesce(cd_plano::text, '') = '';

		If (qt_regra_conv_w > 0) then
			Return 'S';
		Else	Return 'N';
		End if;

	elsif (ie_opcao_p = 'CC')
	and (cd_categoria_p IS NOT NULL AND cd_categoria_p::text <> '') then

		select	count(*)
		into STRICT	qt_regra_categoria_w
		from	convenio_doc_atend
		where	cd_convenio = cd_convenio_p
		and	cd_categoria = cd_categoria_p
		and	coalesce(cd_plano::text, '') = '';

		If (qt_regra_categoria_w > 0) then
			Return 'S';
		Else	Return 'N';
		End if;

	elsif (ie_opcao_p = 'PC')
	and (cd_plano_p IS NOT NULL AND cd_plano_p::text <> '') then

		select	count(*)
		into STRICT	qt_regra_plano_w
		from	convenio_doc_atend
		where	cd_convenio = cd_convenio_p
		and	cd_plano = cd_plano_p;

		If (qt_regra_plano_w > 0) then
			Return 'S';
		Else	Return 'N';
		End if;

	End if;

End if;

Return 'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
