CREATE OR REPLACE FUNCTION obter_se_reg_fim_consulta_pepa ( nr_seq_consulta_pepo_p bigint, nr_seq_item_pronto_p bigint) RETURNS varchar AS $body$
DECLARE


    qt_horas_apos_consulta_p integer;
    qt_diff_hours_p bigint;
    dt_liberacao_p timestamp;
    ie_lib_p varchar(1) := 'N';


BEGIN

  select qt_horas_novo_registro
  into STRICT qt_horas_apos_consulta_p
  from perfil_item_pepa
  where cd_perfil = wheb_usuario_pck.get_cd_perfil
  and nr_seq_item_pront = nr_seq_item_pronto_p;

  if (coalesce(qt_horas_apos_consulta_p,0) > 0) then
    select dt_liberacao
    into STRICT dt_liberacao_p
    from atend_consulta_pepa
    where nr_sequencia = nr_seq_consulta_pepo_p;

    select coalesce(pkg_date_utils.get_DiffDate(dt_liberacao_p, clock_timestamp(), 'HOUR'),0)
    into STRICT qt_diff_hours_p
;

    if (qt_diff_hours_p >= 0 and qt_diff_hours_p <= qt_horas_apos_consulta_p) then
      ie_lib_p := 'S';
    end if;
  else
    select coalesce(max('S'),'N')
    into STRICT ie_lib_p
    from atend_consulta_pepa
    where nr_sequencia = nr_seq_consulta_pepo_p
    and coalesce(dt_liberacao::text, '') = ''
    and coalesce(dt_inativacao::text, '') = '';
  end if;

  return ie_lib_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
