CREATE OR REPLACE FUNCTION obter_se_reg_num ( ie_tipo_numeracao_p text, nr_declaracao_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w	varchar(10)	:= 'S';
qt_registro_w	bigint;


BEGIN


select	count(*)
into STRICT	qt_registro_w
from	regra_numeracao_declaracao a,
	REGRA_NUMERACAO_DEC_ITEM b
where	a.nr_sequencia	= b.NR_SEQ_REGRA_NUM
and	a.ie_situacao	= 'A'
and	a.ie_tipo_numeracao	= ie_tipo_numeracao_p;


if (qt_registro_w	> 0) and (nr_declaracao_p IS NOT NULL AND nr_declaracao_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	regra_numeracao_declaracao a,
		REGRA_NUMERACAO_DEC_ITEM b
	where	a.nr_sequencia	= b.NR_SEQ_REGRA_NUM
	and	a.ie_situacao	= 'A'
	and	a.ie_tipo_numeracao	= ie_tipo_numeracao_p
	and	nr_declaracao	= nr_declaracao_p
	and	IE_DISPONIVEL	= 'S';

	if (qt_registro_w	= 0) then
		ds_retorno_W	:= 'N';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
