CREATE OR REPLACE FUNCTION obter_se_reg_sae_braden (nr_atendimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp) RETURNS varchar AS $body$
DECLARE


C01 CURSOR FOR
SELECT (select	coalesce(max('Possui SAE'),'SS')
		 from 	pe_prescricao b
		 where	b.nr_atendimento = a.nr_atendimento
		 and	b.dt_prescricao between a.dt_liberacao and a.dt_liberacao + 1
		 and	(b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '')
		 and	coalesce(b.dt_inativacao::text, '') = '') ie_sae
from atend_escala_braden a
where   nr_atendimento = nr_atendimento_p
and		dt_liberacao between dt_inicio_p and dt_fim_p
and		qt_ponto < 19;

ie_sae_w 	  varchar(255);


BEGIN

open C01;
loop
fetch C01 into
	ie_sae_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_sae_w = 'SS') then
		return 'NÃ£o possui SAE';
	end if;
	end;
end loop;
close C01;

return ie_sae_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
