CREATE OR REPLACE FUNCTION obter_se_reingresso_uti_atend ( dt_inicial_p timestamp, dt_final_p timestamp, nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_atendimento_w		bigint;
cd_classif_setor_w		varchar(2);				
cd_classif_setor_ant_w	varchar(2);
qt_uti_w			integer	:= 0;
vl_retorno_w		real	:= 0;
qt_reingresso_uti_w		integer	:= 0;
qt_internacao_uti_w	integer	:= 0;
nr_atendimento_ant_w	bigint;
ie_saiu_uti_w		varchar(1);
				
--verifica o atendimento 
C01 CURSOR FOR 
	SELECT	nr_Atendimento 
	from	atendimento_paciente 
	where	dt_entrada between dt_inicial_p and dt_final_p 
	and	nr_atendimento = nr_atendimento_p;
		
--verifica as passagens de setor do paciente		 
C02 CURSOR FOR 
	SELECT	obter_classif_setor(cd_setor_atendimento) 
	from	atend_paciente_unidade 
	where	nr_atendimento	= nr_atendimento_w 
	order by 	dt_entrada_unidade;
			

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_atendimento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	ie_saiu_uti_w	:= 'S';
	open C02;
	loop 
	fetch C02 into	 
		cd_classif_setor_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		--verifica se é a primeira passagem pela uti, ou se a passagem anterior não é por uti e a passagem atual é pela uti 
		--verifica se é reingresso 
		if	((coalesce(cd_classif_setor_ant_w::text, '') = '') or (cd_classif_setor_ant_w <> '4')) and (ie_saiu_uti_w = 'S') and (cd_classif_setor_w = '4') then 
			qt_uti_w	:= qt_uti_w + 1;
			ie_saiu_uti_w	:= 'N';
		end if;
 
		if (cd_classif_setor_w	= '3') then 
			ie_saiu_uti_w	:= 'S';
		end if;
		cd_classif_setor_ant_w	:= cd_classif_Setor_w;
		end;
	end loop;
	close C02;
	end;
	qt_uti_w	:= qt_uti_w - 1;
	if (qt_uti_w > 0) then 
		qt_reingresso_uti_w	:= qt_reingresso_uti_w + qt_uti_w;
	end if;
	cd_classif_setor_ant_w	:= null;
	qt_uti_w	:= 0;
end loop;
 
vl_retorno_w	:= qt_reingresso_uti_w;
 
return	coalesce(vl_retorno_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
