CREATE OR REPLACE FUNCTION obter_se_relatorio_lib_pais2 ( nr_seq_relatorio_p bigint, cd_pais_relat_p bigint) RETURNS varchar AS $body$
DECLARE



ie_pais_relat_w		varchar(15);
ie_user_locale_w		varchar(15);
cd_pais_relat_w		integer;
ie_liberado_w		varchar(1) := 'S';


BEGIN

cd_pais_relat_w		:= cd_pais_relat_w;

ie_pais_relat_w 	:= OBTER_PAIS_RELAT(cd_pais_relat_w);
ie_user_locale_w 	:= coalesce(PKG_I18N.GET_ESTAB_LOCALE, 'pt_BR');

select	CASE WHEN		ie_user_locale_w='1' THEN  'pt_BR' WHEN 		ie_user_locale_w='2' THEN  'es_MX' WHEN 		ie_user_locale_w='5' THEN  'en_US' WHEN 		ie_user_locale_w='7' THEN  'de_DE' WHEN 		ie_user_locale_w='8' THEN  'ar_SA' WHEN 		ie_user_locale_w='9' THEN  'en_AU' WHEN 		ie_user_locale_w='13' THEN  'es_DO'  ELSE ie_user_locale_w END 
			
into STRICT	ie_user_locale_w
;

if (coalesce(cd_pais_relat_w::text, '') = '') then
	ie_liberado_w := 'S';
elsif (ie_pais_relat_w = ie_user_locale_w) then
	ie_liberado_w := 'S';
else
	ie_liberado_w := 'N';
end if;

return ie_liberado_w;

end;
$body$
