SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_repasse_liberado ( nr_seq_propaci_p bigint, nr_seq_matpaci_p bigint) RETURNS varchar AS $body$
DECLARE


ie_repasse_liberado_w	varchar(1);


BEGIN

if (nr_seq_propaci_p IS NOT NULL AND nr_seq_propaci_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_repasse_liberado_w
	from	procedimento_paciente a
	where	exists (SELECT	1
		from	procedimento_repasse x
		where	x.ie_status		in ('D','G','R','S','L','P')
		and	x.nr_seq_procedimento	= a.nr_sequencia)
	and	a.nr_sequencia	= nr_seq_propaci_p;

else

	select	coalesce(max('S'),'N')
	into STRICT	ie_repasse_liberado_w
	from	material_atend_paciente a
	where	exists (SELECT	1
		from	material_repasse x
		where	x.ie_status		in ('D','G','R','S','L','P')
		and	x.nr_seq_material	= a.nr_sequencia)
	and	a.nr_sequencia	= nr_seq_matpaci_p;

end if;

RETURN ie_repasse_liberado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
